mapper.select:
select user_id userId
from users
where user_name = #{userName} and user_phone = #{userPhone}

mapper.update:
update users
set user_pw = #{userPw}
where user_id = #{userId}

update users
set user_name = #{userName}, user_nickname = #{userNickname},
user_loc_si = #{userLocSi}, user_loc_gu  = #{userLocGu},
user_loc_dong = #{userLocDong}, user_birth = #{userBirth},
user_phone = #{userPhone}
where user_id = #{userId}
